import { useState } from 'react';

const useInventory = () => {
  const [items, setItems] = useState([]);
  const [message, setMessage] = useState('');

  const clearMessage = () => {
    setMessage('');
  };

  const addItem = (newItem) => {
    if (!newItem.name || !newItem.quantity) {
      setMessage('Item name and quantity are required.');
      return;
    }
    if (items.find(item => item.name === newItem.name)) {
      setMessage('Item already exists in the inventory.');
      return;
    }
    setItems([...items, newItem]);
    setMessage('The item was added to the inventory.');
  };

  const updateItem = (updatedItem) => {
    const index = items.findIndex(item => item.id === updatedItem.id);
    if (index === -1) {
      setMessage('Item not found.');
      return;
    }
    setItems(items.map(item => (item.id === updatedItem.id ? updatedItem : item)));
    setMessage('The item was updated in the inventory.');
  };

  const removeItem = (itemId) => {
    if (!itemId) {
      setMessage('Item ID is required to remove an item.');
      return;
    }
    setItems(items.filter(item => item.id !== itemId));
    setMessage('The item was removed from the inventory.');
  };

  const getAllItems = () => {
    return items;
  };

  return {
    addItem,
    updateItem,
    removeItem,
    getAllItems,
    message,
    clearMessage,
  };
};

export default useInventory;
