import React, { useState } from 'react';

function UpdateItem({ updateItem }) {
  const [searchName, setSearchName] = useState('');
  const [updatedItem, setUpdatedItem] = useState({ name: '', quantity: '', price: '' });

  const handleUpdate = (e) => {
    e.preventDefault();
    updateItem(searchName, updatedItem); // Call the update function from App.js
    setUpdatedItem({ name: '', quantity: '', price: '' });
  };

  return (
    <form onSubmit={handleUpdate}>
      <input
        type="text"
        placeholder="Enter item name to update"
        value={searchName}
        onChange={(e) => setSearchName(e.target.value)}
      />
      <input
        type="number"
        placeholder="New quantity"
        value={updatedItem.quantity}
        onChange={(e) => setUpdatedItem({ ...updatedItem, quantity: e.target.value })}
      />
      <input
        type="number"
        placeholder="New price"
        value={updatedItem.price}
        onChange={(e) => setUpdatedItem({ ...updatedItem, price: e.target.value })}
      />
      <button type="submit">Update Item</button>
    </form>
  );
}

export default UpdateItem;
