import React from 'react';
import AddItem from './components/AddItem';
import UpdateItem from './components/UpdateItem';
import RemoveItem from './components/RemoveItem';
import DisplayItems from './components/DisplayItems';
import SearchItem from './components/SearchItem';
import SortItems from './components/SortItems';
import LowStockItems from './components/LowStockItems';
import useInventory from './Inventory/useInventory';
import './styles.css';

function App() {
  const {
    addItem,
    updateItem,
    removeItem,
    getItemById,
    getItemsByCategory,
    getAllItems,
    getLowStockItems,
    sortItems,
  } = useInventory();

  return (
    <div className="App">
      <h1>Inventory Management System</h1>

      <section className="section">
        <h2>Add Item</h2>
        <AddItem addItem={addItem} />
      </section>

      <section className="section">
        <h2>Update Item</h2>
        <UpdateItem updateItem={updateItem} />
      </section>

      <section className="section">
        <h2>Remove Item</h2>
        <RemoveItem removeItem={removeItem} />
      </section>

      <section className="section">
        <h2>Display Items</h2>
        <DisplayItems getItemsByCategory={getItemsByCategory} getAllItems={getAllItems} />
      </section>

      <section className="section">
        <h2>Search Item</h2>
        <SearchItem getItemById={getItemById} />
      </section>

      <section className="section">
        <h2>Sort Items</h2>
        <SortItems sortItems={sortItems} />
      </section>

      <section className="section">
        <h2>Low Stock Items</h2>
        <LowStockItems getLowStockItems={getLowStockItems} />
      </section>
    </div>
  );
}

export default App;
