import { useState } from 'react';

function useInventory() {
  const [inventory, setInventory] = useState([]);

  const addItem = (newItem) => {
    setInventory([...inventory, newItem]);
  };

  const updateItem = (name, updatedData) => {
    setInventory(
      inventory.map(item =>
        item.name === name ? { ...item, ...updatedData } : item
      )
    );
  };

  const removeItem = (name) => {
    setInventory(inventory.filter(item => item.name !== name));
  };

  const getItemById = (id) => {
    return inventory.find(item => item.name === id) || null;
  };

  const getAllItems = () => inventory;


  const getLowStockItems = () => {
    return inventory.filter(item => item.quantity < 5);
  };

  const sortItems = (criteria) => {
    const sorted = [...inventory].sort((a, b) => {
      if (criteria === 'name') return a.name.localeCompare(b.name);
      if (criteria === 'quantity') return a.quantity - b.quantity;
      if (criteria === 'price') return a.price - b.price;
      return 0;
    });
    setInventory(sorted);
  };

  return {
    addItem,
    updateItem,
    removeItem,
    getItemById,
    getAllItems,
    getLowStockItems,
    sortItems,
  };
}

export default useInventory;
