import React, { useState } from 'react';
import AddItem from './AddItem';
import RemoveItem from './RemoveItem';
import UpdateItem from './UpdateItem';
import SearchItem from './SearchItem';
import SortItems from './SortItems';
import InventoryTable from './InventoryTable';
import './styles.css';

const App = () => {
  const [items, setItems] = useState([]);

  const addItem = (newItem) => {
    setItems([...items, newItem]);
  };

  const removeItem = (id) => {
    const updatedItems = items.filter(item => item.id !== id);
    setItems(updatedItems);
    return updatedItems.length < items.length ? 'Item removed!' : 'Item not found!';
  };

  const updateItem = (id, field, newValue) => {
    const updatedItems = items.map(item => 
      item.id === id ? { ...item, [field]: newValue } : item
    );
    setItems(updatedItems);
    return 'Item updated!';
  };

  const getItemById = (id) => {
    return items.find(item => item.id === id) || null;
  };

  const sortItems = (field, order) => {
    const sortedItems = [...items].sort((a, b) => {
      if (order === 'ascending') {
        return a[field] - b[field];
      }
      return b[field] - a[field];
    });
    return sortedItems;
  };

  return (
    <div className="App">
      <h1>Inventory Management System</h1>
      <AddItem addItem={addItem} />
      <RemoveItem removeItem={removeItem} />
      <UpdateItem updateItem={updateItem} />
      <SearchItem getItemById={getItemById} />
      <SortItems sortItems={sortItems} />
      <InventoryTable items={items} />
    </div>
  );
};

export default App;
