import React, { useState } from 'react';
import AddItem from './components/AddItem';
import UpdateItem from './components/UpdateItem';
import RemoveItem from './components/RemoveItem';
import DisplayItems from './components/DisplayItems';
import SearchItem from './components/SearchItem';
import SortItems from './components/SortItems';
import LowStockItems from './components/LowStockItems';
import Notification from './components/Notification';
import useInventory from './Inventory/UseInventory';
import './styles.css';

function App() {
  const {
    addItem,
    updateItem,
    removeItem,
    getItemById,
    getAllItems,
    getLowStockItems,
    sortItems,
    clearMessage,
  } = useInventory();

  const [selectedComponent, setSelectedComponent] = useState('');
  const [message, setMessage] = useState(''); // Define the message state

  const renderComponent = () => {
    switch (selectedComponent) {
      case 'AddItem':
        return <AddItem addItem={addItem} showMessage={showMessage} />;
      case 'UpdateItem':
        return <UpdateItem updateItem={updateItem} getItemById={getItemById} showMessage={showMessage} />;
      case 'RemoveItem':
        return <RemoveItem removeItem={removeItem} showMessage={showMessage} />;
      case 'DisplayItems':
        return <DisplayItems getAllItems={getAllItems} />;
      case 'SearchItem':
        return <SearchItem getItemById={getItemById} showMessage={showMessage} />;
      case 'SortItems':
        return <SortItems sortItems={sortItems} showMessage={showMessage} />;
      case 'LowStockItems':
        return <LowStockItems getLowStockItems={getLowStockItems} />;
      default:
        return <p>Select an action to get started.</p>;
    }
  };

  const showMessage = (msg) => {
    clearMessage();
    setMessage(msg);
  };

  return (
    <div className="App">
      <h1>Inventory Management System</h1>
      <h3>made by Arvin Zoleta</h3>

      <div className="button-container">
        <button onClick={() => setSelectedComponent('AddItem')}>Add Item</button>
        <button onClick={() => setSelectedComponent('UpdateItem')}>Update Item</button>
        <button onClick={() => setSelectedComponent('RemoveItem')}>Remove Item</button>
        <button onClick={() => setSelectedComponent('DisplayItems')}>Display Items</button>
        <button onClick={() => setSelectedComponent('SearchItem')}>Search Item</button>
        <button onClick={() => setSelectedComponent('SortItems')}>Sort Items</button>
        <button onClick={() => setSelectedComponent('LowStockItems')}>Low Stock Items</button>
      </div>

      {message && (
        <Notification message={message} clearMessage={clearMessage} />
      )}

      <div className="component-container">
        {renderComponent()}
      </div>
    </div>
  );
}

export default App;
