import React from 'react';

function DisplayItems({ getAllItems }) {
  const items = getAllItems();

  return (
    <div>
      <h2>All Items</h2>
      {items.length === 0 ? (
        <p>No items in inventory</p>
      ) : (
        <ul>
          {items.map((item, index) => (
            <li key={index}>
              {item.name} - {item.quantity} pcs - ${item.price}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default DisplayItems;
