import React, { useState } from 'react';
import AddItem from './AddItem';
import DisplayItems from './DisplayItems';
import LowStockItems from './LowStockItems';
import RemoveItem from './RemoveItem';
import SearchItem from './SearchItem';
import SortItems from './SortItems';
import UpdateItem from './UpdateItem';

const App = () => {
  const [items, setItems] = useState([]);

  const addItem = (newItem) => {
    setItems((prevItems) => [...prevItems, newItem]);
    return "Item added.";
  };

  const removeItem = (id) => {
    const itemExists = items.some(item => item.id === id);
    if (itemExists) {
      setItems((prevItems) => prevItems.filter(item => item.id !== id));
      return "Item removed.";
    }
    return "Item not found.";
  };

  const updateItem = (id, field, newValue) => {
    const itemIndex = items.findIndex(item => item.id === id);
    if (itemIndex !== -1) {
      const updatedItems = [...items];
      updatedItems[itemIndex][field] = newValue;
      setItems(updatedItems);
      return "Item updated.";
    }
    return "Item not found.";
  };

  const getItemById = (id) => {
    return items.find(item => item.id === id);
  };

  const getLowStockItems = () => {
    return items.filter(item => item.quantity < 5); // Example low stock threshold
  };

  const sortItems = (field, order) => {
    const sorted = [...items].sort((a, b) => {
      if (order === 'ascending') {
        return a[field] > b[field] ? 1 : -1;
      } else {
        return a[field] < b[field] ? 1 : -1;
      }
    });
    return sorted;
  };

  return (
    <div className="App">
      <AddItem addItem={addItem} />
      <DisplayItems getAllItems={() => items} getItemsByCategory={(category) => items.filter(item => item.category === category)} />
      <LowStockItems getLowStockItems={getLowStockItems} />
      <RemoveItem removeItem={removeItem} />
      <SearchItem getItemById={getItemById} />
      <SortItems sortItems={sortItems} />
      <UpdateItem updateItem={updateItem} />
    </div>
  );
};

export default App;
