import React, { useState } from 'react';

function UpdateItem({ updateItem }) {
  const [name, setName] = useState('');
  const [updatedData, setUpdatedData] = useState({ quantity: '', price: '' });

  const handleUpdate = (e) => {
    e.preventDefault();
    if (name && (updatedData.quantity || updatedData.price)) {
      updateItem(name, updatedData);
      setName(''); // Reset after update
      setUpdatedData({ quantity: '', price: '' });
    }
  };

  return (
    <div>
      <h2>Update Item</h2>
      <form onSubmit={handleUpdate}>
        <input
          type="text"
          placeholder="Item Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        <input
          type="number"
          placeholder="New Quantity"
          value={updatedData.quantity}
          onChange={(e) =>
            setUpdatedData({ ...updatedData, quantity: e.target.value })
          }
        />
        <input
          type="number"
          placeholder="New Price"
          value={updatedData.price}
          onChange={(e) =>
            setUpdatedData({ ...updatedData, price: e.target.value })
          }
        />
        <button type="submit">Update Item</button>
      </form>
    </div>
  );
}

export default UpdateItem;
